<template>
  <div ref="charts01"
       class="echarts charts-01">
  </div>
</template>

<script>
import * as echarts from 'echarts'
// import 'echarts-gl'
let N = -12
export default {
  data() {
    return {
      animation: false
    }
  },
  methods: {
    time() {
      if (this.timer) {
        clearInterval(this.timer)
      }
      let self = this
      this.timer = setInterval(() => {
        N--
        if (N === -20) {
          N = -12
        }
        self.setChart()
      }, 150)
    },
    setChart() {
      var xData = ['A', 'B', 'C', 'D', 'E']
      var data = [55, 40, 77, 57, 20]
      var percent = [21, 28, 51, 35, 15]
      var star =
        'data:image/png;base64,'
      var imgList = []
      var iconData = []
      data.forEach((item, index) => {
        imgList.push({
          coord: [index, item],
          symbolSize: [100, 50],
          symbolKeepAspect: true,
          symbolOffset: [-40, -185],
          symbol: 'image://' + star
        })
        iconData.push({
          value: item,
          symbolPosition: 'end'
        })
      })
      // console.log(iconData)
      const option = {
        // backgroundColor: 'rgb(8,25,34)',
        tooltip: {
          trigger: 'axis',
          backgroundColor: 'rgba(7,55,63,0.7)',
          borderColor: 'rgba(7,55,63,0.7)',
          textStyle: {
            color: '#fff'
          },
          formatter: function (params) {
            return params[0].marker + '' + params[0].name + ': ' + params[0].value
          }
        },
        grid: {
          bottom: '20%',
          right: 0,
          left: 0
        },
        xAxis: {
          data: xData,
          axisTick: {
            show: false
          },
          axisLine: {
            show: false
          },
          axisLabel: {
            show: true,
            textStyle: {
              color: '#fff'
            }
          }
        },
        yAxis: {
          splitLine: {
            show: false
          },
          axisTick: {
            show: false
          },
          axisLine: {
            show: false
          },
          axisLabel: {
            show: false
          }
        },
        series: [
          {
            name: '权属情况',
            type: 'pictorialBar',
            symbolSize: [50, 12.5],
            symbolOffset: [0, -10],
            z: 12,
            itemStyle: {
              normal: {
                color: '#14b1eb',
                opacity: 0.5
              }
            },
            data: iconData
          },
          {
            name: '',
            type: 'pictorialBar',
            symbolSize: [65, 15],
            symbolOffset: [0, -2],
            z: 10,
            itemStyle: {
              normal: {
                color: 'transparent',
                borderColor: '#14b1eb',
                borderType: 'solid',
                borderWidth: 5
              }
            },
            data: iconData
          },
          {
            name: '',
            type: 'pictorialBar',
            symbolSize: [50, 12.5],
            symbolOffset: [0, -5],
            z: 10,
            itemStyle: {
              normal: {
                color: 'transparent',
                borderColor: '#14b1eb',
                borderType: 'solid',
                borderWidth: 5
              }
            },
            data: iconData
          },
          {
            name: '',
            type: 'pictorialBar',
            symbolSize: [25, 5],
            symbolOffset: [0, -5],
            z: 12,
            itemStyle: {
              normal: {
                color: 'rgb(9,227,255)'
              }
            },
            data: iconData
          },
          {
            type: 'bar',
            itemStyle: {
              color: {
                x: 0,
                y: 0,
                x2: 0,
                y2: 1,
                type: 'linear',
                global: false,
                colorStops: [
                  {
                    offset: 0,
                    color: 'rgb(24,118,158)'
                  },
                  {
                    offset: 1,
                    color: 'rgba(9,30,41,0.5)' //底部渐变颜色
                  }
                ]
              }
            },
            silent: true,
            barWidth: 50,
            barGap: '-100%', // Make series be overlap
            data: data,
            label: {
              show: true,
              position: 'top',
              distance: 15,
              color: '#fff',
              fontSize: 12,
              formatter: params => {
                // console.log(params)
                return '{a|' + params.value + '}' + '株' + '\n' + '{b|' + percent[params.dataIndex] + '%}'
              },
              rich: {
                a: {
                  color: '#03adff',
                  fontSize: 18,
                  fontWeight: 'bold'
                },
                b: {
                  color: '#fff',
                  fontSize: 16,
                  marginTop: 15
                }
              }
            },
            markPoint: {
              data: imgList
            }
          }
        ]
      }
      let charts01 = this.charts01
      if (!charts01) {
        const div = this.$refs.charts01
        charts01 = echarts.init(div, null, { renderer: 'svg' })
        let l = function () {
          charts01.resize()
        }
        this.$erd.listenTo(div, l)
        this.charts01 = charts01
      }
      charts01.clear()
      charts01.setOption(option)
      charts01.resize()
    }
  },
  mounted() {
    this.setChart()
    // this.time()
  },
  beforeDestroy() {
    if (this.timer) {
      clearInterval(this.timer)
    }
  }
}
</script>

<style lang="scss" scoped>
.echarts {
  width: 100%;
  height: 100%;
}
</style>